import React from 'react';
import './styles.css';
import { Title, Text, TextInput, Button } from 'react-native-paper';
import 'bootstrap/dist/css/bootstrap.min.css';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

class Register extends React.Component{

    state = {
        email: '',
        username: '',
        password: '',
        confirmPass: ''
    };

    constructor() {
        super();
        this.register = this.register.bind(this);
    }    

    render() {
        return(
            <div className="fullpage">
                <Container className="main">
                    <Row>
                        <Col md={{span:4, offset:4}}>
                            <Title>Register!</Title>
                            <TextInput value={this.state.email} label='email' onChangeText={email => this.setState({ email })}/>
                            <TextInput value={this.state.username} label='username' onChangeText={username => this.setState({ username })}/>
                            <TextInput value={this.state.password} label='password' onChangeText={password => this.setState({ password })}/>
                            <TextInput value={this.state.confirmPass} label='confirm password' onChangeText={confirmPass => this.setState({ confirmPass })}/>
                            <Button mode="contained" onClick={this.register}>Sign Up!</Button>
                            <Text>Already have an account?</Text>
                            <a href="">Login</a>
                        </Col>
                    </Row>
                </Container>
            </div>
        );
    }

    register() {
        let isGood = true;
        //Check each text input
        if(this.state.email === ''){
            //handle missing email
            isGood = false;
        }
        if(this.state.username === ''){
            //handle missing username
            isGood = false;
        }
        if(this.state.password == ''){
            //handle missing password
            isGood = false;
        }
        if(this.state.confirmPass == ''){
            //handle missing comfirmPass
            isGood = false;
        }

        //Check if email is formatted correctly

        //Check if password is longer than 8 characters
        if(this.state.password.length < 8){
            //handle short password
            isGood = false;
        }
        //Check if passwords match
        if(this.state.password !== this.state.confirmPass){
            //handle mismatched passwords
            isGood = false;
        }

        if(isGood){
            //Register user into database
            alert('Everything is Good!');
        } else {
            alert('Something\'s not Good');
        }
    }

}

export default Register;
