import React from 'react';
import './styles.css';
import { Title, Text, TextInput, Button } from 'react-native-paper';
import 'bootstrap/dist/css/bootstrap.min.css';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

class Register extends React.Component{

    state = {
        email: '',
        username: '',
        password: '',
        confirmPass: '',
        emailErr: false,
        usernameErr: false,
        passwordErr: false,
        passconfErr: false
    };

    render() {
        return(
            <div className="fullpage">
                <Container className="main">
                    <Row>
                        <Col md={{span:4, offset:4}}>
                            <Title>Register!</Title>
                            <TextInput error={this.state.emailErr} value={this.state.email} label='email' onChangeText={email => this.setState({ email })}/>
                            <TextInput error={this.state.usernameErr} value={this.state.username} label='username' onChangeText={username => this.setState({ username })}/>
                            <TextInput error={this.state.passwordErr} value={this.state.password} label='password' onChangeText={password => this.setState({ password })}/>
                            <TextInput error={this.state.passconfErr} value={this.state.confirmPass} label='confirm password' onChangeText={confirmPass => this.setState({ confirmPass })}/>
                            <Button mode="contained" onClick={this.register}>Sign Up!</Button>
                            <Text>Already have an account?</Text>
                            <a href="">Login</a>
                        </Col>
                    </Row>
                </Container>
            </div>
        );
    }

    register = () => {
        let isFilledOut = true;
        this.setState({emailErr: false});
        this.setState({usernameErr: false});
        this.setState({passwordErr: false});
        this.setState({passconfErr: false});

        if(this.state.email === ''){
            this.setState({emailErr: true});
            isFilledOut = false;
        }
        if(this.state.username === ''){
            //handle missing username
            this.setState({usernameErr: true});
            isFilledOut = false;
        }
        if(this.state.password == ''){
            //handle missing password
            this.setState({passwordErr: true});
            isFilledOut = false;
        }
        if(this.state.confirmPass == ''){
            //handle missing comfirmPass
            this.setState({passconfErr: true});
            isFilledOut = false;
        }

        //Check if email is formatted correctly

        //Check if password is longer than 8 characters
        if(this.state.password.length < 8){
            //handle short password
            isFilledOut = false;
        }
        //Check if passwords match
        if(this.state.password !== this.state.confirmPass){
            //handle mismatched passwords
            isFilledOut = false;
        }

        if(isFilledOut){
            //Register user into database
            alert('Everything is Good!');
        } else {
            alert('Something\'s not Good');
        }
    }

}

export default Register;
